mapper.select:
SELECT *
FROM bubble_sort
WHERE practice_id = #{practiceId} AND process_num > #{processNum}
ORDER BY process_num ASC
    LIMIT 1;

SELECT curr_list
FROM bubble_sort
WHERE practice_id = #{practiceId} AND process_num = #{processNum}

SELECT curr_list
FROM bubble_sort
WHERE practice_id = #{practiceId} AND process_num = (
    SELECT MAX(process_num) FROM bubble_sort WHERE practice_id = #{practiceId}
)

mapper.insert:
INSERT INTO bubble_sort (
    exchange, pre_pos, post_pos, curr_list, practice_id, process_num, user_id, turn
) VALUES (
    #{exchange}, #{prePos}, #{postPos}, #{currList}, #{practiceId}, #{processNum}, #{userId}, #{turn}
)

mapper.select:
SELECT COUNT(*)
FROM bubble_sort
WHERE user_id = #{userId}
  AND practice_id = #{practiceId}
