mapper.select:
SELECT *
FROM bubble_sort
WHERE practice_id = #{practiceId} AND process_num = #{processNum}

SELECT curr_list
FROM bubble_sort
WHERE practice_id = #{practiceId} AND process_num = #{processNum}

SELECT curr_list
FROM bubble_sort
WHERE practice_id = #{practiceId} AND process_num = (
    SELECT MAX(process_num) FROM bubble_sort WHERE practice_id = #{practiceId}
)
