mapper.select:
SELECT *
FROM select_sort
WHERE practice_id = #{practiceId} AND process_num > #{processNum}
ORDER BY process_num ASC
LIMIT 1;

SELECT curr_list
FROM select_sort
WHERE practice_id = #{practiceId} AND process_num = #{processNum}

SELECT curr_list
FROM select_sort
WHERE practice_id = #{practiceId} AND process_num = (
    SELECT MAX(process_num) FROM select_sort WHERE practice_id = #{practiceId}
)

mapper.insert:
INSERT INTO select_sort (
    exchange, min_pos, cur_pos, curr_list, practice_id, process_num, user_id, turn
) VALUES (
             #{exchange}, #{minPos}, #{curPos}, #{currList}, #{practiceId}, #{processNum}, #{userId}, #{turn}
         )
