mapper.select:
SELECT COUNT(DISTINCT problem_id)
FROM (
         SELECT problem_id
         FROM study_history
         WHERE sort_method = #{sortMethod}
           AND user_id = #{userId}
           AND status = 1
         GROUP BY problem_id
         HAVING MAX(curr_time)
     ) AS unique_problems

SELECT *
FROM study_history
WHERE user_id = #{userId}
  AND sort_method = #{sortMethod}
  AND problem_id = #{problemId}
  AND status = 0
ORDER BY curr_time DESC
    LIMIT 1
